sys_variable_value.value:
(function execute(inputs, outputs) {
	   // Get the configuration item record
   var ciGr = new GlideRecord('x_976337_license_c_u_cmdb_ci_licenses');
   if (ciGr.get(inputs.configuration_item)) {
      // Create a new catalog item record
      var catalogItemGr = new GlideRecord('sc_cat_item');
      catalogItemGr.initialize();
      catalogItemGr.setValue('name', ciGr.getValue('name'));
      catalogItemGr.setValue('short_description', ciGr.getValue('short_description'));
      catalogItemGr.setValue('variables', 'c36b24dcc35921101e62241ce0013114'); 
      catalogItemGr.setValue('price', ciGr.getValue('price'));
     catalogItemGr.setValue('publisher', ciGr.getValue('publisher'));
     catalogItemGr.setValue('cmdbref', ciGr.getUniqueValue());
      var catalogItemSysId = catalogItemGr.insert();
      if (catalogItemSysId) {
         gs.info('Catalog item created successfully.');
      } else {
         gs.error('Catalog item creation failed.');
      }
   } else {
      gs.error('Configuration item not found.');
   }

})(inputs, outputs);

